mapper.select:
select q.id, q.description, q.title, q.type_id as "typeId", q.create_user_id as "createUserId", q.create_time as createTime, q.update_time as updateTime from `question` q where 1=1
and q.id = #{id}
and q.description LIKE CONCAT(CONCAT('%', #{description}), '%')
and q.title LIKE CONCAT(CONCAT('%', #{title}), '%')
and q.type_id = #{typeId}
and q.create_user_id = #{createUserId}
order by q.create_time desc
